'use strict';

var ToNumber = require('es-abstract/2019/ToNumber');

var $isNaN = require('es-abstract/helpers/isNaN');
var $isFinite = require('es-abstract/helpers/isFinite');
var $sign = require('es-abstract/helpers/sign');

var abs = require('es-abstract/2019/abs');
var floor = require('es-abstract/2019/floor');
var modulo = require('es-abstract/2019/modulo');

// https://ecma-international.org/ecma-262/6.0/#sec-touint8

module.exports = function ToUint8(argument) {
	var number = ToNumber(argument);
	if ($isNaN(number) || number === 0 || !$isFinite(number)) { return 0; }
	var posInt = $sign(number) * floor(abs(number));
	return modulo(posInt, 0x100);
};
