'use strict'

var transport = exports

// Exports utils
transport.utils = require('spdy-transport/lib/spdy-transport/utils')

// Export parser&framer
transport.protocol = {}
transport.protocol.base = require('spdy-transport/lib/spdy-transport/protocol/base')
transport.protocol.spdy = require('spdy-transport/lib/spdy-transport/protocol/spdy')
transport.protocol.http2 = require('spdy-transport/lib/spdy-transport/protocol/http2')

// Window
transport.Window = require('spdy-transport/lib/spdy-transport/window')

// Priority Tree
transport.Priority = require('spdy-transport/lib/spdy-transport/priority')

// Export Connection and Stream
transport.Stream = require('spdy-transport/lib/spdy-transport/stream').Stream
transport.Connection = require('spdy-transport/lib/spdy-transport/connection').Connection

// Just for `transport.connection.create()`
transport.connection = transport.Connection
