"use strict";

/** @typedef {import("webpack-dev-server/lib/Server").ClientConnection} ClientConnection */

// base class that users should extend if they are making their own
// server implementation
module.exports = class BaseServer {
  /**
   * @param {import("webpack-dev-server/lib/Server")} server
   */
  constructor(server) {
    /** @type {import("webpack-dev-server/lib/Server")} */
    this.server = server;

    /** @type {ClientConnection[]} */
    this.clients = [];
  }
};
